@import "./styles/tokens.css";

@tailwind base;
@tailwind components;
@tailwind utilities;

@layer base {
  html, body {
    @apply bg-[var(--color-bg)] text-[var(--color-fg)] antialiased;
  }
}

/**
 * UI Component Exports
 * 
 * All reusable UI components are exported from here.
 * Import as: import { Button, Select, ConfirmDialog } from '@/components/ui'
 */

// Button
export { Button, buttonVariants } from './Button'
export type { ButtonProps } from './Button'

// Spinner
export { Spinner } from './Spinner'

// Select
export { Select } from './Select'
export type { SelectProps, SelectOption } from './Select'

// Dialog System
export {
  // Base components
  Dialog,
  DialogPortal,
  DialogOverlay,
  DialogTrigger,
  DialogClose,
  DialogContent,
  DialogHeader,
  DialogBody,
  DialogFooter,
  DialogTitle,
  DialogDescription,
  // Pre-built dialogs
  AlertDialog,
  ConfirmDialog,
  PromptDialog,
  CustomDialog,
  // Types
  type DialogVariant,
  type AlertDialogProps,
  type ConfirmDialogProps,
  type PromptDialogProps,
  type CustomDialogProps,
} from './Dialog'

// Dialog Provider & Hook
export { 
  DialogProvider, 
  useDialog,
  type AlertOptions,
  type ConfirmOptions,
  type PromptOptions,
} from './DialogProvider'

// Add more component exports as they are created:
// export { Card } from './Card'
// export { Input } from './Input'
// export { Badge } from './Badge'
// export { Toast } from './Toast'
// export { Table } from './Table'
// export { EmptyState } from './EmptyState'
// export { Loading } from './Loading'

/**
 * Button Component
 * 
 * A versatile button component with multiple variants and sizes.
 * 
 * CUSTOMIZATION: Update the className values to match your design tokens from tokens.css.
 * Look for comments marked "STYLE:" to find customization points.
 * 
 * Usage:
 *   <Button variant="default">Click me</Button>
 *   <Button variant="destructive" size="sm">Delete</Button>
 *   <Button variant="outline" disabled>Disabled</Button>
 */

import * as React from 'react'
import { cva, type VariantProps } from 'class-variance-authority'
import { cn } from '@/lib/utils'

const buttonVariants = cva(
  // STYLE: Base button styles
  'inline-flex items-center justify-center rounded-[var(--radius-md)] text-sm font-medium transition-colors focus-visible:outline-none disabled:pointer-events-none disabled:opacity-50',
  {
    variants: {
      variant: {
        // STYLE: Default variant - update colors to use your tokens
        default: 'bg-[var(--color-accent)] text-white hover:bg-[var(--color-accent-hover)]',
        // STYLE: Destructive variant - update colors to use your tokens
        destructive: 'bg-[var(--color-error)] text-white hover:opacity-90',
        // STYLE: Outline variant - update colors to use your tokens
        outline: 'border border-[var(--color-border)] bg-[var(--color-bg)] text-[var(--color-fg)] hover:bg-[var(--color-surface)]',
        // STYLE: Secondary variant - update colors to use your tokens
        secondary: 'bg-[var(--color-surface)] text-[var(--color-fg)] hover:opacity-80',
        // STYLE: Ghost variant - update colors to use your tokens
        ghost: 'text-[var(--color-fg)] hover:bg-[var(--color-surface)]',
        // STYLE: Link variant - update colors to use your tokens
        link: 'text-[var(--color-accent)] underline-offset-4 hover:underline',
      },
      size: {
        // STYLE: Size dimensions - adjust as needed
        default: 'h-9 px-4 py-2',
        sm: 'h-8 px-3 text-xs',
        lg: 'h-11 px-8',
        icon: 'h-9 w-9',
      },
    },
    defaultVariants: {
      variant: 'default',
      size: 'default',
    },
  }
)

export interface ButtonProps
  extends React.ButtonHTMLAttributes<HTMLButtonElement>,
    VariantProps<typeof buttonVariants> {}

const Button = React.forwardRef<HTMLButtonElement, ButtonProps>(
  ({ className, variant, size, ...props }, ref) => (
    <button
      className={cn(buttonVariants({ variant, size, className }))}
      ref={ref}
      {...props}
    />
  )
)
Button.displayName = 'Button'

export { Button, buttonVariants }

/**
 * Select Component (Radix-based)
 * 
 * A fully accessible, styleable select component built on Radix UI primitives.
 * 
 * CUSTOMIZATION: Update the className values to match your design tokens from tokens.css.
 * Look for comments marked "STYLE:" to find customization points.
 * 
 * Usage:
 *   <Select
 *     value={status}
 *     onValueChange={setStatus}
 *     options={[
 *       { value: 'active', label: 'Active' },
 *       { value: 'inactive', label: 'Inactive' },
 *     ]}
 *     placeholder="Select status"
 *   />
 */

import * as SelectPrimitive from '@radix-ui/react-select'
import { Check, ChevronDown } from 'lucide-react'
import { cn } from '@/lib/utils'

export interface SelectOption {
  value: string
  label: string
}

export interface SelectProps {
  value: string
  onValueChange: (value: string) => void
  options: SelectOption[]
  placeholder?: string
  disabled?: boolean
  className?: string
  emptyMessage?: string
}

export function Select({
  value,
  onValueChange,
  options,
  placeholder = 'Select...',
  disabled = false,
  className,
  emptyMessage = 'No options available',
}: SelectProps) {
  const isEmpty = options.length === 0

  return (
    <SelectPrimitive.Root value={value} onValueChange={onValueChange} disabled={disabled || isEmpty}>
      <SelectPrimitive.Trigger
        className={cn(
          // STYLE: Trigger base styles - update colors to use your tokens
          'flex h-10 w-full items-center justify-between rounded-md border px-3 py-2 text-sm',
          // STYLE: Default state colors
          'bg-[var(--color-bg)] border-[var(--color-border)] text-[var(--color-fg)]',
          // STYLE: Placeholder color
          'data-[placeholder]:text-[var(--color-muted)]',
          // STYLE: Focus state
          'focus:outline-none focus:border-[var(--color-accent)]',
          // STYLE: Hover state
          'hover:border-[var(--color-border-hover)]',
          // STYLE: Disabled/empty state
          'disabled:cursor-not-allowed disabled:opacity-50',
          className
        )}
      >
        <SelectPrimitive.Value placeholder={placeholder} />
        <SelectPrimitive.Icon asChild>
          <ChevronDown className="h-4 w-4 opacity-50" />
        </SelectPrimitive.Icon>
      </SelectPrimitive.Trigger>

      <SelectPrimitive.Portal>
        <SelectPrimitive.Content
          className={cn(
            // STYLE: Dropdown content styles
            'relative z-50 min-w-[8rem] overflow-hidden rounded-md border shadow-md',
            // STYLE: Content colors
            'bg-[var(--color-bg)] border-[var(--color-border)] text-[var(--color-fg)]',
            // STYLE: Animation
            'data-[state=open]:animate-in data-[state=closed]:animate-out',
            'data-[state=closed]:fade-out-0 data-[state=open]:fade-in-0',
            'data-[state=closed]:zoom-out-95 data-[state=open]:zoom-in-95',
            'data-[side=bottom]:slide-in-from-top-2 data-[side=top]:slide-in-from-bottom-2'
          )}
          position="popper"
          sideOffset={4}
        >
          <SelectPrimitive.Viewport className="p-1">
            {isEmpty ? (
              // STYLE: Empty state message
              <div className="py-2 px-3 text-sm text-[var(--color-muted)]">
                {emptyMessage}
              </div>
            ) : (
              // Filter out empty values - Radix Select doesn't support empty string values
              options.filter((option) => option.value !== '').map((option) => (
                <SelectPrimitive.Item
                  key={option.value}
                  value={option.value}
                  className={cn(
                    // STYLE: Item base styles
                    'relative flex w-full cursor-pointer select-none items-center rounded-sm py-1.5 pl-8 pr-2 text-sm outline-none',
                    // STYLE: Item hover/focus state
                    'focus:bg-[var(--color-surface)] focus:text-[var(--color-fg)]',
                    // STYLE: Disabled item
                    'data-[disabled]:pointer-events-none data-[disabled]:opacity-50'
                  )}
                >
                  <span className="absolute left-2 flex h-3.5 w-3.5 items-center justify-center">
                    <SelectPrimitive.ItemIndicator>
                      <Check className="h-4 w-4" />
                    </SelectPrimitive.ItemIndicator>
                  </span>
                  <SelectPrimitive.ItemText>{option.label}</SelectPrimitive.ItemText>
                </SelectPrimitive.Item>
              ))
            )}
          </SelectPrimitive.Viewport>
        </SelectPrimitive.Content>
      </SelectPrimitive.Portal>
    </SelectPrimitive.Root>
  )
}

export default Select

/**
 * API Client Configuration
 *
 * ⚠️ DO NOT MODIFY THIS FILE - It is a protected boilerplate file.
 *
 * To add API endpoints, create a NEW file like `src/services/myApi.ts`:
 *
 *   import { api, PaginatedResponse } from './api'
 *
 *   // Django REST Framework returns paginated responses by default
 *   export const myApi = {
 *     // Option 1: Extract just the results array (simple usage)
 *     list: async () => {
 *       const response = await api.get<PaginatedResponse<Item>>('/api/items/')
 *       return response.data.results
 *     },
 *
 *     // Option 2: Return full pagination (when you need count/next/previous)
 *     listPaginated: async (page?: number) => {
 *       const response = await api.get<PaginatedResponse<Item>>('/api/items/', { params: { page } })
 *       return response.data
 *     },
 *   }
 *
 * ❌ NEVER define your own API_BASE_URL - import from here if needed:
 *   import { API_BASE_URL } from './api'
 *
 * Uses relative URLs by default (works with Vite proxy).
 * Set VITE_API_URL in .env only if you need a full URL.
 */
import axios from 'axios'

// Use env variable if set, otherwise use relative URLs (proxy handles routing)
// Export for services that need the base URL (rare - prefer using the api instance)
export const API_BASE_URL = import.meta.env.VITE_API_URL || ''

/**
 * Django REST Framework paginated response shape.
 * All list endpoints return this format by default.
 */
export interface PaginatedResponse<T> {
  count: number          // Total number of items across all pages
  next: string | null    // URL to next page, or null if last page
  previous: string | null // URL to previous page, or null if first page
  results: T[]           // Items for current page
}

export const api = axios.create({
  baseURL: API_BASE_URL,
  headers: {
    'Content-Type': 'application/json',
  },
})

// Request interceptor for auth tokens
api.interceptors.request.use((config) => {
  const token = localStorage.getItem('access_token')
  if (token) {
    config.headers.Authorization = `Bearer ${token}`
  }
  return config
})

// Response interceptor for error handling
api.interceptors.response.use(
  (response) => response,
  (error) => {
    // Handle 401 - redirect to login
    if (error.response?.status === 401) {
      localStorage.removeItem('access_token')
      // Optionally redirect to login
    }
    return Promise.reject(error)
  }
)

/**
 * Extract error message from various error formats
 */
export function getErrorMessage(error: unknown): string {
  if (axios.isAxiosError(error)) {
    // DRF error format
    const data = error.response?.data
    if (typeof data === 'string') return data
    if (data?.detail) return data.detail
    if (data?.message) return data.message
    if (data?.non_field_errors) return data.non_field_errors[0]
    // Field errors
    if (typeof data === 'object') {
      const firstKey = Object.keys(data)[0]
      if (firstKey && Array.isArray(data[firstKey])) {
        return `${firstKey}: ${data[firstKey][0]}`
      }
    }
    return error.message
  }
  if (error instanceof Error) return error.message
  return 'An unexpected error occurred'
}

/**
 * Fetch all pages from a paginated endpoint.
 * Use sparingly - prefer pagination UI for large datasets.
 *
 * @example
 * const allItems = await fetchAllPages<Item>('/api/items/')
 */
export async function fetchAllPages<T>(url: string): Promise<T[]> {
  const allResults: T[] = []
  let nextUrl: string | null = url

  while (nextUrl !== null) {
    const response: { data: PaginatedResponse<T> } = await api.get<PaginatedResponse<T>>(nextUrl)
    allResults.push(...response.data.results)
    nextUrl = response.data.next
  }

  return allResults
}

/**
 * E2E Tests - MUST BE CUSTOMIZED FOR YOUR APP
 *
 * These tests capture screenshots for visual validation.
 * You MUST edit them to match your app's actual pages and flows.
 *
 * See: .claude/instructions/CLAUDE.md -> "Final Validation Steps" for details.
 *
 * Required screenshots:
 * - MainPage.png: Your app's main/home page (after auth if applicable)
 * - LandingPage.png: Landing or auth page (or duplicate of main if no auth)
 */

import { test, expect } from '@playwright/test'
import { mkdirSync, existsSync } from 'node:fs'
import { dirname, join } from 'node:path'
import { fileURLToPath } from 'node:url'

// DO NOT CHANGE THESE NAMES
const MAIN_PAGE_SCREENSHOT_NAME = 'MainPage'
const LANDING_PAGE_SCREENSHOT_NAME = 'LandingPage'

// Ensure screenshots directory exists (ESM-compatible)
const __filename = fileURLToPath(import.meta.url)
const __dirname = dirname(__filename)
const screenshotsDir = join(__dirname, '..', 'screenshots')
if (!existsSync(screenshotsDir)) {
  mkdirSync(screenshotsDir, { recursive: true })
}

test.describe('App E2E Tests', () => {
  /**
   * EDIT THIS TEST: Capture your landing/auth page
   *
   * If your app has authentication:
   * - Navigate to the login or register page
   * - Verify auth UI elements are present
   *
   * If your app has no auth:
   * - Duplicate the MainPage test logic here
   */
  test('captures LandingPage screenshot', async ({ page }) => {
    // TODO: Update this route to your app's landing/auth page
    await page.goto('/')

    await page.waitForLoadState('networkidle')
    // start with screenshot
    await page.screenshot({
      path: join(screenshotsDir, LANDING_PAGE_SCREENSHOT_NAME + '.png'),
      fullPage: true,
    })

    await page.waitForTimeout(500)
    // TODO: Add assertions for your landing page elements
    // Example: await expect(page.getByTestId('auth.login.form')).toBeVisible()

    await expect(page).toHaveTitle(/.+/)
  })

  /**
   * EDIT THIS TEST: Capture your main app page
   *
   * Update this test to:
   * 1. Navigate to your app's actual main page route (e.g., /dashboard, /home)
   * 2. Handle authentication if required
   * 3. Wait for key elements using data-testid selectors
   * 4. Verify the main functionality is visible
   */
  test('captures MainPage screenshot', async ({ page }) => {
    // TODO: Update this route to your app's main page
    await page.goto('/')

    await page.waitForLoadState('networkidle')
    // start with screenshot
    await page.screenshot({
      path: join(screenshotsDir, MAIN_PAGE_SCREENSHOT_NAME + '.png'),
      fullPage: true,
    })

    // TODO: Replace this generic auth handling with your app's actual flow
    // Example for apps with auth:
    const loginButton = page.getByTestId('auth.login.submit')
    if (await loginButton.isVisible({ timeout: 1000 }).catch(() => false)) {
      const emailInput = page.getByTestId('auth.login.email')
      const passwordInput = page.getByTestId('auth.login.password')

      if (await emailInput.isVisible({ timeout: 500 }).catch(() => false)) {
        await emailInput.fill('user@example.com')
      }
      if (await passwordInput.isVisible({ timeout: 500 }).catch(() => false)) {
        await passwordInput.fill('testpassword123')
      }
      await loginButton.click()
      await page.waitForLoadState('networkidle')
    }

    await page.waitForTimeout(500)

    // TODO: Add assertions for your main page elements
    // Example: await expect(page.getByTestId('projects.list')).toBeVisible()

    await expect(page).toHaveTitle(/.+/)
  })
})

